_cpp_format = """
// ---------------------------------------------------
// This file is autogenerated by git-version-builder.
// DO NOT MODIFY!
// ---------------------------------------------------

#pragma once
#ifndef __GITVERSIONBUILDER__VERSION_H__
#define __GITVERSIONBUILDER__VERSION_H__

namespace version {
  constexpr const char *VERSION_STRING = "%s";
  constexpr const char *TAG_NAME = "%s";
  constexpr const unsigned int COMMITS_SINCE_TAG = %d;
  constexpr const char *GIT_COMMIT_ID = "%s";
}

#endif
"""

_python_format = """
# ---------------------------------------------------
# This file is autogenerated by git-version-builder.
# DO NOT MODIFY!
# ---------------------------------------------------

VERSION_STRING = "%s"
TAG_NAME = "%s"
COMMITS_SINCE_TAG = %d
GIT_COMMIT_ID = "%s"
"""


def _output(version_info, format):
    return format % (
        version_info.version_string, version_info.tag_name, version_info.commits_since_tag, version_info.commit_id)


def to_cpp(version_info):
    return _output(version_info, _cpp_format)


def to_python(version_info):
    return _output(version_info, _python_format)